definition.script:
pipeline {
    agent any

    stages 
    {
        stage('Start') {
            steps {
                sh 'rm -fr $NEST_FOLDER_PUBLISH'
            }
        }

        stage('Restore') {
            steps {
                sh 'ssh $NEST_CI_BUILD_MACHINE dotnet restore $NEST_FOLDER_SOURCE'
            }
        }

        stage('Build') {
            steps {
                sh 'ssh $NEST_CI_BUILD_MACHINE dotnet clean --configuration Release $NEST_FOLDER_SOURCE'
                sh 'ssh $NEST_CI_BUILD_MACHINE dotnet build --configuration Release --output $NEST_FOLDER_PUBLISH $NEST_FOLDER_SOURCE'
            }
        }

        stage('Test') {
            steps {
                sh 'ssh $NEST_CI_BUILD_MACHINE dotnet test --configuration Release $NEST_FOLDER_SOURCE /p:CollectCoverage=true /p:CoverletOutputFormat=cobertura'
                sh 'ssh $NEST_CI_BUILD_MACHINE /root/.dotnet/tools/reportgenerator -reports:$NEST_FOLDER_SOURCE/*/*/coverage.cobertura.xml -targetdir:$NEST_FOLDER_PUBLISH/reports'
            }
        }

        stage('Report') {
            steps {
                publishHTML([allowMissing: false, alwaysLinkToLastBuild: false, keepAll: false, reportDir: '/var/app/publish/reports', reportFiles: 'index.htm', reportName: 'Code Coverage Report', reportTitles: ''])
            }
        }
    }
}
